catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {


	if (isLoading ) {
		return;
	}


	//Type appropriate comment here, and begin script below
	g_form.clearOptions('option_2');
	if (newValue == 'parent1') {
		g_form.addOption('option_2', '','-- None --');
		g_form.addOption('option_2', 'child1','Child 1');
		g_form.addOption('option_2', 'child2','Child 2');
		g_form.addOption('option_2', 'child3','Child 3');

	} else if (newValue == 'parent2') {
		g_form.addOption('option_2', '','-- None --');
		g_form.addOption('option_2', 'child4','Child 4');
		g_form.addOption('option_2', 'child5','Child 5');

	} else if (newValue == 'parent3') {
		g_form.addOption('option_2', '','-- None --');
		g_form.addOption('option_2', 'child6','Child 6');
		g_form.addOption('option_2', 'child7','Child 7');
	} else {
		g_form.addOption('option_2', '','-- None --');
	}

}
